const { items } = await actions["agi/improvement"].findFeedback({
  uri: `workflow://${context.location?.workflowId}/${context.location?.nodeId}`,
  search: context.transcript.map((x) => `${x.role}: ${x.content}`).join("\n"),
});

if (items.length) {
  context.instructions += `

    ## Learnings from Previous Experiences

    ${items
      .map(
        (x, i) => `### Learning ${i + 1}
    ${x.learning}
    `
      )
      .join("\n")}
    `;
}

